INSERT INTO assessments (slug, name, description, category, total_questions, scoring_method, interpretation_ranges) VALUES
('neurodiversity-traits', 'Neurodiversity Traits Profile', 'A comprehensive self-assessment of neurodivergent traits across 8 categories. Rate each trait from 1 (Does not apply) to 5 (Core part of my experience).', 'custom', 0, 'sum', 
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 1, '**Difficulty with eye contact**: The experience of feeling physically or mentally uncomfortable when making direct eye contact with another person. You might look at a person''s mouth, nose, or forehead instead, or look away frequently during conversations. (Example: During a business meeting, you find yourself staring at the wall or your notes to avoid looking at your colleagues, which can sometimes be misinterpreted as disinterest.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 2, '**Trouble reading facial expressions**: Difficulty understanding the emotions that others are conveying through their facial movements. (Example: A friend tells you a story while smiling, but you can''t tell if they are genuinely happy or if they are using a polite, "masking" smile to hide sadness.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 3, '**Difficulty with social cues (e.g., non-verbal communication, body language)**: Struggling to understand and respond to the unspoken rules and signals of social interaction, such as body language, tone of voice, or gestures. (Example: You are talking to someone who keeps leaning away from you and looking at the door, but you don''t realize this means they are trying to end the conversation.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 4, '**Difficulty with small talk**: Finding brief, casual conversations about superficial topics (like the weather) uninteresting, frustrating, or pointless. (Example: A coworker asks, "How was your weekend?" and you feel a strong urge to avoid the question because it feels like a meaningless exchange with no real purpose.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 5, '**Difficulty initiating conversations**: A struggle to start a conversation with someone, often due to not knowing what to say or how to begin. (Example: You want to talk to a new neighbor, but you stand awkwardly outside your door for several minutes, unable to think of an appropriate opening line.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 6, '**Difficulty maintaining conversations**: Challenges in keeping a conversation going, often by not knowing how to respond, ask follow-up questions, or transition to a new topic. (Example: A friend is telling you about their favorite movie, but you don''t know what to say in response, leading to an awkward silence.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 7, '**Difficulty ending conversations**: Not knowing how to appropriately and smoothly exit a conversation without seeming rude or abrupt. (Example: You are ready to leave a gathering, so you simply turn and walk away without saying "goodbye" because you don''t know the proper social procedure.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 8, '**Difficulty with tone of voice**: The inability to use the correct vocal inflection to convey a specific meaning or emotion, often resulting in a tone that is flat, monotone, or doesn''t match the words being said. (Example: You say "I''m so excited!" in a flat, monotone voice, and your partner asks if you''re feeling okay because you don''t sound excited at all.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 9, '**Difficulty with inflection in voice**: Not varying your tone to emphasize important words or meanings within a sentence. (Example: You are telling a story and your voice stays at the same pitch and volume for the entire time, making it hard for listeners to tell what the most important parts are.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 10, '**Difficulty with volume control**: Not being able to gauge whether your speaking volume is appropriate for the environment, leading you to speak too loudly in quiet spaces or too softly in loud ones. (Example: You''re at a library and are asked to quiet down because you''re unaware that you''ve been speaking loudly enough for people at the other end of the room to hear you.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 11, '**Difficulty with pitch**: Speaking in a flat, unchanging pitch, which can make your voice sound robotic or expressionless. (Example: You are explaining a complex topic and your voice remains at a consistent pitch, lacking the variations that would typically express excitement or curiosity.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 12, '**Difficulty with intonation**: The inability to vary the pitch and speed of your voice to convey appropriate meaning or emotion. (Example: When asking a question, your voice doesn''t rise at the end, so it sounds like a statement instead of an inquiry.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 13, '**Difficulty with prosody**: Struggling with the overall rhythm, stress, and melody of speech that helps convey meaning and emotion. (Example: Your speech lacks a natural "flow" and might sound stilted or mechanical to others.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 14, '**Difficulty in articulation**: Struggling to make clear and distinct speech sounds, which can make it hard for others to understand you. (Example: You know the words you want to say, but they come out jumbled or slurred, especially when you are tired or stressed.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 15, '**Difficulty with vocabulary**: Having a limited vocabulary or struggling to find the right words to express yourself. (Example: You have a clear thought in your mind, but you can''t recall the specific words needed to articulate it, so you use simpler or more general terms.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 16, '**Difficulty with grammar**: Struggling with the rules and structure of language, leading to sentences that may be grammatically incorrect or confusing. (Example: You might accidentally use the wrong verb tense or mix up sentence structure, such as saying "He go to the store" instead of "He went to the store.")', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 17, '**Difficulty with syntax**: Struggling with the correct order and arrangement of words in a sentence. (Example: When speaking, you might place words in an unusual order, like "The dog, blue and fast, ran" instead of "The fast blue dog ran.")', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 18, '**Difficulty with semantics**: Struggling with the meaning and interpretation of words and phrases, which can lead to misunderstandings. (Example: Someone says, "I''m feeling blue," and you are confused because you literally think they are a color, not that they are sad.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 19, '**Echolalia**: The repetition of words, phrases, or sounds that you have heard from others. (Example: After your partner says, "Let''s go to the store," you find yourself repeating the phrase, "Go to the store," under your breath.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 20, '**Unique communication styles**: Having a way of communicating that is different from the social norm, which may include being quirky, abrupt, overly emotional, asking many questions, interrupting frequently, or being very direct. (Example: You get so excited about a topic that you interrupt a conversation to ask a series of very specific, rapid-fire questions.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 21, '**Preference for written communication**: Finding it easier and more comfortable to communicate through writing, such as texts or emails, because they offer clarity, are less ambiguous, and are harder to misinterpret. (Example: You would rather send a detailed email to a coworker explaining a project than have a face-to-face discussion about it.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 22, '**Requirement for extra processing time in social interactions and communications**: Needing more time to absorb and understand what is being said and to formulate a response. (Example: Someone asks you a question, and there''s a noticeable pause before you respond because you are processing the information and thinking about your answer.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 23, '**Tendency to monologue**: Talking at length about passionate topics without realizing you are dominating the conversation or that others may have lost interest. (Example: A friend asks about your favorite video game, and you end up talking for 30 minutes straight about all its intricate details without letting them get a word in.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 24, '**Difficulty with implicit rules and social norms**: Struggling to understand the unwritten rules and conventions of social situations, which can lead to misunderstandings or social blunders. (Example: You do not realize that in a work meeting, it''s rude to correct your boss in front of everyone, even if you know they''ve made a factual error.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 25, '**Difficulty with ambiguous instructions**: Needing simple, precise, and step-by-step instructions to complete a task. (Example: Your partner says, "Can you help out around the house?" and you have no idea where to start because the instruction is too vague. You''d prefer, "Please load the dishwasher and then fold the laundry.")', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 26, '**Tendency to collapse power differentials in social interactions, viewing everyone as equal**: A natural inclination to interact with people as equals, regardless of their social status, age, or title. (Example: You talk to your CEO with the same casual, direct manner you would use with a new friend, which might be perceived as disrespectful.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 27, '**Sharing ''SAME Stories'' as a way to acknowledge deep listening and empathy**: A communication style where you respond to someone''s story by sharing a similar, personal experience to show that you''ve heard them and can relate. This is a form of empathy. (Example: A friend tells you they are struggling with a difficult project at work, and you respond with, "I had a similar experience when I was working on my last project. It was so hard when...")', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 28, '**More direct communication manner**: Communicating in a straightforward, honest way, often without a "social filter." (Example: Your friend asks for your opinion on a new outfit, and you say, "I don''t think that color suits you," without realizing this might be considered rude.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 29, '**Difficulty not pointing out inaccuracies**: A strong urge to correct factual errors, even in casual conversation, to the point where it can disrupt the flow or be seen as confrontational. (Example: During a group discussion, someone says a statistic is "about 50%," and you feel compelled to interject, "Actually, the exact number is 47.3%.")', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 30, '**Often seen as "loners" or having very limited social groups**: A preference for your own company and a tendency to have a small circle of close, trusted friends rather than a large group of acquaintances. (Example: You are invited to a party but would much rather spend the evening at home working on a hobby you love.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 31, '**Less reliance on emotionally satiating others**: Not feeling a strong need to make others feel comfortable or to engage in emotional labor (e.g., comforting someone with a white lie). (Example: You don''t feel obligated to give a long hug or a series of reassurances to a friend who is upset if you don''t feel that it''s a genuine or productive use of your time.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 32, '**Etiquette breaches (e.g., not understanding social boundaries)**: Unintentionally violating social rules or boundaries. (Example: You ask a new acquaintance a very personal question because you don''t realize it''s considered inappropriate for casual conversation.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 33, '**Communication style often interpreted as telling jokes or being comical when stating observations**: Your direct, literal observations might be perceived as humorous by others, even when you are being serious. (Example: You point out a very specific, quirky detail about a public space, and everyone laughs, but you were simply stating an observation, not trying to be funny.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 34, '**Operating on only one level in thinking and communication style ("what you see is what you get")**: A preference for straightforward, simple communication with no hidden meanings or subtext. (Example: You mean exactly what you say and assume others do too, so you become confused or frustrated by sarcasm, flirting, or subtle hints.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 35, '**Literal thinking**: Struggling to understand figurative language, idioms, metaphors, and abstract concepts like money or time. (Example: When someone says, "It''s raining cats and dogs," you picture animals falling from the sky and are confused.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 36, '**Difficulty with abstract thinking**: A preference for concrete, factual information over abstract, hypothetical concepts. (Example: You struggle to grasp the concept of "justice" unless it''s explained with a very specific, real-world example of a crime and punishment.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 37, '**Difficulty with Central Coherence**: A tendency to focus on small details rather than seeing the big picture or connecting information into a larger, meaningful context. (Example: You can memorize every street name and turn for a trip, but you get lost because you can''t form a mental map of the city as a whole.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 38, '**Attention to detail**: A natural inclination to notice and focus on the minutia and inner workings of things. (Example: You can spot a single typo in a large document almost instantly or notice a very small change in a room''s decor that no one else sees.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 39, '**Strong memory and knowledge retention**: Having an exceptional memory for facts, conversations, and specific information, especially related to your special interests. (Example: You can recall a conversation you had five years ago in precise detail, including what everyone was wearing and what the weather was like.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 40, '**Enhanced pattern recognition**: The ability to see and remember patterns in data, the environment, and human behavior at a higher level than most people. (Example: You can quickly identify the repeating sequences in a complex data set or notice behavioral patterns in a group of people that no one else has observed.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 41, '**Difficulty recognizing faces (prosopagnosia)**: A struggle to remember or recognize faces, even those of people you know well, often leading you to rely on other cues like clothing, voice, or hairstyle. (Example: You have to mentally check someone''s outfit and hair before you can confidently say "hello" to them in a crowded room.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 42, '**Divergent thinking**: The ability to think creatively and make connections across seemingly unrelated ideas, leading to "outside the box" solutions. (Example: When asked to solve a problem, you come up with a solution that combines ideas from two different fields, like engineering and art, that no one else would have thought of.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 43, '**Altered time perception**: Struggling to accurately gauge the passage of time. (Example: A task that should only take 15 minutes feels like "an eternity," or you might hyperfocus on a project and not realize that several hours have passed.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 44, '**High levels of creativity**: Looking at things from a different perspective, leading to unique and often quirky ideas or solutions. (Example: You are great at coming up with imaginative stories, art, or designs because you naturally approach problems from a non-traditional angle.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 45, '**Difficulty understanding sarcasm**: A struggle to interpret verbal irony where the speaker''s words mean the opposite of what they are literally saying. (Example: Someone says, "Oh, great, more paperwork," in a sarcastic tone, and you genuinely think they are happy about getting more work.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 46, '**Difficulty with humor**: A struggle to understand jokes, puns, or comedic timing. (Example: You are told a joke, and you have to pause to analyze the wordplay or punchline before you can understand why it''s supposed to be funny.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 47, '**Low tolerance for ambiguity**: A strong preference for clear, concise, and unambiguous communication. You often see things in "black and white." (Example: When a project manager gives a vague deadline, you feel stressed and need to ask several clarifying questions to get an exact date and time.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 48, '**Literal interpretations of figurative language**: Taking idioms, metaphors, and similes at their face value. (Example: Your friend says, "I''m going to hit the hay," and you wonder why they would physically strike a bale of straw.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 49, '**Difficulty in lie telling explained by strong inclination to "systemize"**: A natural preference for systems that are predictable and consistent, which makes lying—a deviation from a predictable system of truth—feel wrong or uncomfortable. (Example: Your brain''s "system" for reality is based on facts, and creating a lie feels like breaking that system, which causes distress.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 50, '**Social anxiety**: Experiencing significant fear or anxiety in social situations, often due to a fear of being judged, misunderstood, or saying the wrong thing. (Example: You get an invitation to a party and immediately feel a sense of dread, picturing all the ways you might mess up a social interaction.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 51, '**Trouble understanding or relating to other people''s emotions**: A struggle to identify and empathize with what others are feeling. (Example: A friend is crying, but you don''t know what to do or say because you don''t fully grasp their sadness, so you offer them a tissue and change the subject.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 52, '**Difficulty with emotional regulation**: Struggling to manage and control intense emotions. This can lead to being easily overwhelmed, emotional outbursts, or the inability to express deep feelings. (Example: A small change in your routine causes an intense wave of frustration and tears that you can''t seem to stop, even though you know it''s a minor issue.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 53, '**Low self-esteem, lack confidence, or a dim view of oneself**: Having a poor self-image, often stemming from repeated negative social experiences or a feeling of being different. (Example: You tend to avoid applying for jobs you are qualified for because you don''t believe you are good enough.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 54, '**Highly sensitive to emotional atmospheres**: Being very aware of and affected by the emotions of others, to the point of feeling overwhelmed. (Example: You walk into a room where two people just had an argument, and you immediately feel the tension and discomfort, even if no one says anything.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 55, '**High sensitivity to criticism**: Experiencing constructive or negative feedback intensely, leading to feelings of being disliked, broken, or inadequate. (Example: Your boss gives you a minor note on a project, and you feel rejected and start thinking you''ve failed completely, even though it was a small correction.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 56, '**Rejection Sensitive Dysphoria (RSD)**: Experiencing extreme emotional pain surrounding the perception of being rejected, criticized, or having failed. (Example: Your friend doesn''t reply to a text message for a few hours, and you immediately feel a surge of intense panic and sadness, assuming they are angry with you and are going to end the friendship.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 57, '**Internalized stigma**: A feeling of shame or inadequacy for being autistic, often leading to a belief that you are out of place or inferior. (Example: You might think, "I''m just the odd one out, and I''ll never be able to fit in or have a normal life.")', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 58, '**Often attributes negative autism-related experiences to societal factors**: A tendency to recognize that your struggles are not due to personal failings but rather because you are navigating a world built for neurotypical people. (Example: You realize that your difficulty with eye contact isn''t a "flaw" but a natural part of who you are, and that societal pressure to force it is the real source of your discomfort.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 59, '**Anxiety as an underlying factor for lack of honesty**: Lying not out of malice but out of a deep-seated anxiety about how others will react to the truth. (Example: You lie about finishing a task not because you want to deceive, but because you are terrified of the potential negative response from your boss or partner if you admit you haven''t finished it.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 60, '**Shame due to long feeling inadequate in a neurotypical world**: A deep-seated feeling of inadequacy that has built up over a lifetime of feeling misunderstood or "wrong" for being who you are. (Example: You feel a sense of shame when you make a social mistake, even if it''s minor, because it reinforces a lifetime of similar experiences.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 61, '**Hypersensitivity to emotions**: Experiencing your own emotions at a hyper-level, making them feel more intense and overwhelming. (Example: When you are happy, it''s an exhilarating, all-consuming joy. When you are sad, it feels like an immense, crushing weight.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 62, '**Ability to understand emotions, but may require time to process and interpret them**: You can eventually understand what someone is feeling, but it is not an immediate or intuitive process. (Example: A friend tells you they''re upset, and it takes you some time to mentally work through their words and body language to fully grasp their sadness, after which you can offer a more thoughtful response.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 63, '**Ability to utilize facial expressions to express themselves**: You are capable of using facial expressions to convey emotions, even if they aren''t always in sync with what you''re saying or feeling. (Example: You can purposefully put on a happy face to show you''re enjoying an event, even if you are feeling overwhelmed.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 64, '**Not telling the truth to intentionally offend people**: You are direct and honest, but your intention is not to hurt someone''s feelings. The honest statement is a product of your natural communication style, not a malicious act. (Example: You state an observation that a friend''s new haircut looks messy. You don''t intend to be cruel; you are just being honest because you think that''s the most helpful thing to do.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 65, '**Sensory sensitivities**: Experiencing an over- or under-reaction to sensory stimuli like textures, sounds, sights, or smells. (Example: The hum of a fluorescent light in an office building is a constant, irritating sound that others don''t seem to notice.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 66, '**Hypersensitivity (over-experience) or Hyposensitivity (under-experience) to light, touch, sound, smell, taste**: Your senses are either heightened (hyper) or dulled (hypo) compared to the general population. (Example: Hypersensitivity: The feeling of a wool sweater is unbearably itchy and painful. Hyposensitivity: You don''t notice that your food is too hot until you''ve burned your mouth.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 67, '**Sensory seeking behaviors**: Repetitive movements or behaviors (stimming) used to self-regulate, soothe, or manage sensory input. (Example: You might rub your hair, chew on a pen cap, or tap your fingers on a table to calm yourself down when you feel anxious or overstimulated.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 68, '**Hyposensitivity to certain sensory inputs**: Experiencing a reduced sensitivity to certain sensations, like pain or temperature. (Example: You might not realize you''ve gotten a minor cut or burn until you see the injury because you didn''t feel any pain at the time.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 69, '**Lack of coordination / clumsiness (dyspraxia)**: Challenges with motor skills, spatial awareness, and a tendency to bump into things or drop objects. (Example: You trip over your own feet while walking or repeatedly knock things off a table.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 70, '**Difficulty with fine motor skills**: Challenges with small, precise movements of the hands and fingers. (Example: You find it difficult to tie your shoelaces, button a shirt, or write neatly.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 71, '**Difficulty with gross motor skills**: Challenges with large movements of the body. (Example: You struggle to catch a ball, jump rope, or run without tripping.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 72, '**Difficulty with balance**: A struggle to maintain your equilibrium, which can affect things like standing on one leg or walking on an uneven surface. (Example: You find it difficult to stand still in a line for a long time without swaying or needing to put your hands on your hips for support.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 73, '**Difficulty with proprioception**: Challenges with the sense of where your body is in space. (Example: You might not be able to tell if your arm is raised without looking at it, or you might find it hard to navigate a dark room without bumping into furniture.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 74, '**Difficulty with spatial awareness**: A struggle to be aware of where your body is in relation to other objects or people. (Example: You accidentally bump into people in a crowded store because you don''t intuitively know how much space you take up.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 75, '**Difficulty with visual processing**: Challenges in making sense of visual information. (Example: It''s hard for you to follow a complex map or find a specific item on a cluttered shelf.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 76, '**Difficulty with auditory processing**: A struggle to make sense of auditory information. (Example: When someone gives you a series of verbal instructions, you can only remember the first one or two, and you have to ask them to repeat everything.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 77, '**Difficulty with tactile processing**: A struggle to make sense of tactile information. (Example: You touch an object and feel a sensation, but you have trouble identifying what the object is just by touch.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 78, '**Resistance to being touched**: A strong aversion to physical touch, which can be due to sensory sensitivity. (Example: You dislike hugging, holding hands, or being kissed, and you cut tags out of your clothes because the feel of them is unbearable.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 79, '**Sensitivity to textures**: A strong aversion to certain textures, especially with clothing or food. (Example: You gag when eating soft, squishy foods like mashed bananas, but you love crunchy foods like carrots.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 80, '**Interoception issues**: Difficulty sensing internal body signals, like hunger, thirst, or emotions, which can impact self-care and regulation. (Example: You might not realize you''re hungry until you feel faint, or you might not recognize that you are about to have an emotional meltdown until it''s already happening.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 81, '**Difficulty with transitions**: Struggling to shift from one activity or task to another. (Example: You are deeply engrossed in a book and feel a wave of anxiety or irritation when your partner tells you it''s time to stop and go to dinner.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 82, '**Difficulty with change**: Experiencing anxiety, stress, or a sense of being overwhelmed by unexpected changes to plans or routines. (Example: Your daily commute is rerouted due to construction, and it completely throws off your entire day, causing significant stress.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 83, '**Need for routine**: A strong preference for a set routine or a structured daily schedule to manage anxiety and feel a sense of comfort and control. (Example: You have a morning routine where you do everything in the same order, and if you miss a step, it feels "wrong" and can make you anxious for the rest of the day.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 84, '**Strong interest in very specific topics**: Developing a deep, sometimes obsessive, passion for a particular subject. (Example: You can spend weeks or months intensely researching and learning everything you can about the history of ancient Roman coins, becoming a walking encyclopedia on the subject.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 85, '**Difficulty with executive functioning**: A struggle with the mental processes that control and organize your thoughts and actions, such as managing time, planning, and organizing. (Example: You find it hard to start a new project, break it down into manageable steps, and then follow through with a plan.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 86, '**Difficulty prioritizing**: A struggle to determine which tasks are most important and should be done first. (Example: You have a long to-do list and spend a lot of time paralyzed, unable to decide whether to do the laundry or pay your bills first.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 87, '**Challenges making decisions**: Struggling to make a choice, even with small things, or feeling overwhelmed by too many options. (Example: You spend an hour in the grocery store trying to decide which brand of pasta to buy, feeling anxious about making the "wrong" choice.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 88, '**Difficulty in multitasking**: The inability to focus on or complete more than one task at a time. (Example: You cannot have a conversation while also reading an email or following a recipe.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 89, '**Inward focus to the world at large, dedicating time to specific, special interests**: You turn your attention inward and focus your time and energy on your passions instead of on broad social or worldly pursuits. (Example: While your friends are focused on the latest social trends, you are happily absorbed in your passion for model trains.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 90, '**Hyperfocus and flow state**: Becoming so intensely absorbed in an activity or subject that you lose all sense of time, people, and your own personal needs. This can lead to high productivity but also to neglecting responsibilities. (Example: You start a coding project and don''t realize that you''ve been sitting at your computer for eight hours without eating or taking a break.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 91, '**Intense expertise and interests**: Your passions evolve from simple interests into genuine, in-depth expertise on a subject. (Example: Your interest in a specific band''s music leads you to not only know all their songs but also to be able to identify every musician on every track and describe the recording process in detail.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 92, '**Strong preference for familiarity**: Feeling comfortable and at ease in known places, environments, and routines. (Example: When on vacation, you prefer to eat at the same restaurant every night because you know what to expect and it''s a source of comfort.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 93, '**Strong need for predictability**: Thriving in environments that are consistent and structured, as this provides a sense of comfort and control. (Example: You feel safe and calm when your daily schedule is predictable and you know what''s going to happen each day.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 94, '**Distinctive learning styles**: Needing a specific way to learn, such as hands-on experiences, visual aids, or repetition, that may differ from traditional methods. (Example: You struggle to learn from a lecture alone but can easily grasp a new concept if you can watch a video or follow a step-by-step diagram.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 95, '**Difficulty with time management**: Struggling to estimate how long tasks will take or to manage appointments and deadlines. (Example: You think a project will take an hour, but it ends up taking three, causing you to be late for a meeting.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 96, '**Strong need for autonomy, freedom, and control**: A desire to be in control of your own life and choices, which helps to manage anxiety and feel regulated. (Example: You dislike being micromanaged at work and prefer to be given a task and left alone to complete it in your own way.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 97, '**Reliance on daily activities**: Having seemingly insignificant activities that are actually crucial for maintaining a sense of routine, sameness, and regulation. (Example: Your morning coffee routine is not just about the coffee; it is a vital, non-negotiable part of your day that provides structure and calm.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 98, '**Difficulty with multi-step instructions**: Becoming overwhelmed or shutting down when given instructions with too many steps, leading to confusion and errors. (Example: Your boss gives you a five-part project, and you immediately feel overwhelmed and stressed, unable to even begin.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 99, '**Preference for solo activities**: Enjoying activities by yourself and finding them fulfilling. (Example: You''d rather spend a Saturday afternoon building a model kit alone than attending a social gathering.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 100, '**Difficulty establishing a daily routine**: You know you need a routine, but you struggle to create and maintain a consistent one. (Example: You want to start a consistent exercise routine but find it difficult to stick to the same time and days each week.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 101, '**ADHD dopamine-seeking behavior**: A need for a higher level of stimulation, which can be managed with activities like video games, online socializing, or listening to loud music. (Example: You feel restless and bored, and the only thing that seems to engage your brain is playing a video game for several hours.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 102, '**Not over-committing or saying "yes" impulsively**: A tendency to be more cautious about taking on new responsibilities or social engagements to avoid burnout or anxiety. (Example: You think for a long time before agreeing to a new committee at work, carefully considering whether you have the energy for it.)', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 103, '**Less inclined to tell lies**: A natural preference for telling the truth.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 104, '**Less skilled in delivering sophisticated forms of deception**: You struggle to maintain a believable lie, often giving away clues that you are being dishonest.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 105, '**Appearance of being ''attractively morally innocent''**: You may be perceived as being naturally pure, trustworthy, or naive due to your directness and honesty.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 106, '**Above average moral conscientious objection against deception**: A strong ethical opposition to lying in any form.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 107, '**Lying can be extremely anxiety-provoking**: The act of lying causes a high level of stress and anxiety.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 108, '**Difficulty distinguishing different kinds of falsehoods**: You may see all lies, from a white lie to a serious one, as being equally "wrong."', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 109, '**Difficulty with context sensitivity in conversations about lying**: You struggle to understand when and why it is appropriate to lie in a given situation.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 110, '**Strong sense of fairness and justice**: A powerful sense of right and wrong and a desire for things to be fair.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 111, '**Directness and honesty**: You speak your mind without a "social filter," often being sincere, trustworthy, and authentic.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 112, '**Highest priority in conversation is the communication of facts**: You value factual information above all else in a discussion.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 113, '**Preference for non-sugarcoated honesty, real feedback, and direct truth**: You want the truth, no matter how harsh it is, and you expect the same from others.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
    INSERT INTO assessment_questions (assessment_id, question_number, question_text, response_type, response_options) VALUES (assessment_id_var, 114, '**Dislike of "curated honesty" or rephrased "nice" versions of the truth**: You are frustrated when people soften the truth to make it more palatable.', 'likert', '[{"value":1,"label":"1 - Does not apply"},{"value":2,"label":"2 - Applies sometimes"},{"value":3,"label":"3 - Noticeable part of life"},{"value":4,"label":"4 - Significant part of life"},{"value":5,"label":"5 - Dominant/Core part"}]'::jsonb) ON CONFLICT (assessment_id, question_number) DO NOTHING;
