    UPDATE challenge_tasks 
    SET content_data = '{"modules":[{"id":"d11m1","name":"Brain Science of Shame","duration":"50 min","points":200,"content":{"intro":"The neurobiology of shame and its impact","topics":["Shame activates pain centers in brain","Amygdala and threat response","Impact on prefrontal cortex (rational thinking)","Neuroplasticity and healing shame"],"keyPoints":["Shame is processed as physical pain","It hijacks rational thinking","Chronic shame reshapes brain pathways","New experiences can rewire shame responses"]},"quiz":[{"question":"Shame activates:","options":["Only emotional centers","The same brain regions as physical pain","Logical thinking areas","Memory storage only"],"correct":1,"explanation":"Brain imaging shows shame activates the same regions as physical pain, which is why it hurts so much."}],"exercise":{"type":"journal","title":"Shame Body Scan","prompts":["Where do you feel shame in your body?","What physical sensations arise?","How can you soothe your nervous system?"]}},{"id":"d11m2","name":"Toxic Shame Development","duration":"50 min","points":200,"content":{"intro":"How shame becomes internalized and toxic","topics":["Healthy shame vs toxic shame","Critical messages internalized","Shame-based identity formation","Intergenerational transmission"],"keyPoints":["Toxic shame says ''I am flawed''","Forms from repeated criticism/rejection","Becomes part of identity","Can be unlearned"]},"quiz":[{"question":"Toxic shame develops from:","options":["Single mistakes","Repeated messages that you are defective","Healthy consequences","Appropriate guilt"],"correct":1,"explanation":"Toxic shame develops when you repeatedly receive the message that you are fundamentally flawed or bad, not just that your behavior needs correction."}],"exercise":{"type":"journal","title":"Shame Origin Story","prompts":["What early messages did you receive about your worth?","Who communicated you were ''bad''?","How did this shape your self-concept?"]}},{"id":"d11m3","name":"Shame Resilience (Brené Brown)","duration":"50 min","points":200,"content":{"intro":"Research-based framework for building shame resilience","topics":["Recognizing shame and triggers","Practicing critical awareness","Reaching out for support","Speaking shame (it dies in empathy)"],"keyPoints":["Shame needs 3 things to grow: secrecy, silence, judgment","Empathy is the antidote","Vulnerability is courage","Everyone experiences shame"]},"quiz":[{"question":"According to Brené Brown, shame cannot survive:","options":["Being kept secret","Being spoken with empathy","Self-criticism","Isolation"],"correct":1,"explanation":"Brown''s research shows shame cannot survive being spoken aloud to someone who responds with empathy."}],"exercise":{"type":"journal","title":"Shame Resilience Plan","prompts":["Who are your empathy resources?","How will you recognize shame arising?","What will you tell yourself?"]}}],"dailyChallenge":{"title":"Shame Resilience Practice","description":"Share one shame experience with a trusted person","points":200}}'::jsonb
