    -- Day 15: Advanced DBT Skills
    UPDATE challenge_tasks 
    SET content_data = '{"modules":[{"id":"d15m1","name":"Radical Acceptance","duration":"50 min","points":200,"content":{"intro":"Accepting reality as it is, without approval","topics":["What radical acceptance is (and isn''t)","Accepting vs approving","Pain vs suffering","Turning the mind toward acceptance"],"keyPoints":["Fighting reality creates suffering","Acceptance doesn''t mean you like it","Frees energy for effective action","A practice, not one-time decision"]},"quiz":[{"question":"Radical acceptance means:","options":["Liking or approving of reality","Acknowledging reality as it is without fighting it","Giving up","Becoming passive"],"correct":1,"explanation":"Radical acceptance is acknowledging reality without fighting it, which reduces suffering and allows effective action."}],"exercise":{"type":"journal","title":"Acceptance Practice","prompts":["What reality am I fighting?","What would acceptance look like?","What energy would this free up?"]}},{"id":"d15m2","name":"Walking the Middle Path","duration":"50 min","points":200,"content":{"intro":"Dialectical thinking: holding two truths simultaneously","topics":["Both/and thinking vs either/or","Dialectics in relationships","Validating opposing perspectives","Finding synthesis"],"keyPoints":["Two things can be true at once","Reduces black-and-white thinking","Essential for healthy relationships","Creates space for complexity"]},"quiz":[{"question":"Dialectical thinking recognizes:","options":["Only one truth exists","Two opposing truths can coexist","Everyone is always wrong","Compromise means losing"],"correct":1,"explanation":"Dialectical thinking holds that two seemingly opposite things can both be true simultaneously."}],"exercise":{"type":"journal","title":"Finding the Middle Path","prompts":["What either/or thinking do you use?","Both/and alternative:","Example: I need independence AND connection"]}},{"id":"d15m3","name":"Opposite Action","duration":"50 min","points":200,"content":{"intro":"Acting opposite to emotional urges to change emotions","topics":["When to use opposite action","Common applications for codependency","How it changes brain patterns","Combining with validation"],"keyPoints":["Urge to isolate → reach out","Urge to attack → approach gently","Changes emotion by changing behavior","Breaks automatic patterns"]},"quiz":[{"question":"If you feel urge to over-function for someone, opposite action would be:","options":["Do even more","Step back and let them handle it","Get angry at them","Ignore your feelings"],"correct":1,"explanation":"Opposite action to over-functioning is stepping back, which helps break the codependent cycle."}],"exercise":{"type":"journal","title":"Opposite Action Plan","prompts":["What urge do you want to change?","What opposite action will you take?","How might this change your patterns?"]}}],"dailyChallenge":{"title":"DBT Practice","description":"Use all 3 DBT skills (acceptance, dialectics, opposite action) today","points":200}}'::jsonb
